import React, { Component } from 'react';
import Table from 'react-bootstrap/Table'
import './App.css';
import Movie from "./components/MovieComponent";

class App extends Component {
    state = {
        movies: [],
    };

  componentDidMount() {
      this.getData().then((response) => {
          this.setState({movies: response.results});
          console.log(this.state.movies);
      });
  }

  getData = async () => {
      let response = await fetch('/getPopular');
      return await response.json();
  };

  sortByRating = () => {
      const copyState = {...this.state};
      const sortedByRating = copyState.movies.sort((a, b) => b.vote_average - a.vote_average);
      this.setState({movies: sortedByRating});
  };

  searchForMovie = (event) => {
    const copyState = {...this.state};
    const search = event.target.value;
    const foundMovies = copyState.movies.filter((movie) => movie.title.indexOf(search) > 0);

    this.setState({movies: foundMovies});
  };

  render() {
    return (
        <div className="App">
            <header>
                <input type="text" onChange={this.searchForMovie}></input>
                <button onClick={this.sortByRating}>Sort By Rating</button>
            </header>

            <Table striped bordered hover>
                <thead>
                <tr>
                    <th>id</th>
                    <th>Movie Title</th>
                    <th>Release Data</th>
                    <th>Popularity</th>
                    <th>Average Rating</th>
                    <th>Overview</th>
                </tr>
                </thead>
                <tbody>
                {this.state.movies.map(({id, title, release_date, popularity, vote_average, overview}) => {
                    return <Movie key={id}
                                  title={title}
                                  id={id}
                                  release_date={release_date}
                                  popularity={popularity}
                                  average_rating={vote_average}
                                  overview={overview}/>
                })}
                </tbody>
            </Table>
        </div>
    );
  }
}

export default App;
