import React, { Component } from 'react';
import Table from 'react-bootstrap/Table'
import './App.css';
import Movie from "./components/MovieComponent";

class App extends Component {
    state = {
        movies: [],
        originalMovies: []
    };

  componentDidMount() {
      this.getPopularMovies().then((response) => {
          this.setState({movies: response.results, originalMovies: response.results});
      });
  }

  getPopularMovies = async () => {
      const response = await fetch('/getPopular');
      return await response.json();
  };

  sortByRating = () => {
      const copyState = {...this.state};
      const sortedByRating = copyState.movies.sort((a, b) => b.vote_average - a.vote_average);
      this.setState({movies: sortedByRating });
  };

  sortByTitle = () => {
      const copyState = {...this.state};
      const sortedByRating = copyState.movies.sort((a, b) => a.title - a.title);
      this.setState({movies: sortedByRating });
  };

  resetFiltering = () => {
      const copyState = {...this.state};
      this.setState({movies: copyState.originalMovies});
  };

  searchPopularMovies = (event) => {
    const searchTerm = event.target.value.toLowerCase();
    const foundMovies = this.state.originalMovies.filter((movie) => movie.title.toLowerCase().indexOf(searchTerm) >= 0);
    this.setState({movies: foundMovies ? foundMovies : this.state.originalMovies});
  };


  searchNewMovie = async (event) => {
    const searchTerm = event.target.value.toLowerCase();
    const response = await fetch('/getNewMovie', {
        method: 'POST',
        headers: {'Content-Type': 'application/json'},
        body: searchTerm
    });

    return await response.json();
  };

  render() {
    const noMoviesFoundMessage = 'This movie was not found. Please search popular movies again with a different term, or search for a new movie.';
    return (
        <div className="App">
            <header>
                <span>Search Popular Movies: <input type="text" onChange={this.searchPopularMovies}/></span>
                <span>Search New Movie: <input type="text" onChange={this.searchNewMovie}/></span>
                <button onClick={this.sortByRating}>Sort By Rating</button>
                <button onClick={this.sortByTitle}>Sort By Movie Title</button>
                <button onClick={this.resetFiltering}>Reset</button>
            </header>

            <Table striped bordered hover>
                <thead>
                <tr>
                    <th>id</th>
                    <th>Movie Title</th>
                    <th>Release Data</th>
                    <th>Popularity</th>
                    <th>Average Rating</th>
                    <th>Overview</th>
                </tr>
                </thead>
                <tbody>
                {this.state.movies.map(({id, title, release_date, popularity, vote_average, overview}) => {
                    return <Movie key={id}
                                  title={title}
                                  id={id}
                                  release_date={release_date}
                                  popularity={popularity}
                                  average_rating={vote_average}
                                  overview={overview}/>
                })}
                {this.state.movies.length ? null : <tr><td>{noMoviesFoundMessage}</td></tr>}
                </tbody>
            </Table>
        </div>
    );
  }
}

export default App;
