import React, { Component } from 'react';
import Table from 'react-bootstrap/Table'
import Movie from "./components/MovieComponent";
import './App.css';

class App extends Component {
    state = {
        movies: [],
        originalMovies: []
    };

  componentDidMount() {
      this.getPopularMovies().then((response) => {
          this.setState({movies: response.results, originalMovies: response.results});
      });
  }

  getPopularMovies = async () => {
      const response = await fetch('/getPopular');
      return await response.json();
  };

  sortByRating = () => {
      const movies = this.state.movies;
      const sortedByRating = movies.sort((a, b) => b.vote_average - a.vote_average);
      this.setState({movies: sortedByRating });
  };

  sortByTitle = () => {
      const originalMovies = this.state.originalMovies;
      const sortedByTitle = originalMovies.sort(function(a, b) {
          const nameA = a.title.toUpperCase();
          const nameB = b.title.toUpperCase();
          if (nameA < nameB) {
              return -1;
          }
          if (nameA > nameB) {
              return 1;
          }
          return 0;
      });
      this.setState({movies: sortedByTitle });
  };

  resetFiltering = () => {
      const originalMovies = this.state.originalMovies;
      this.setState({movies: originalMovies});
  };

  searchPopularMovies = (event) => {
    const originalMovies = this.state.originalMovies;
    const searchTerm = event.target.value.toLowerCase();
    const foundMovies = originalMovies.filter((movie) => movie.title.toLowerCase().indexOf(searchTerm) >= 0);
    this.setState({movies: foundMovies ? foundMovies : originalMovies});
  };

  getNewMovie = async (event) => {
    const searchTerm = event.target.value.toLowerCase();
    const response = await fetch('/getNewMovie', {
        method: 'POST',
        headers: {'Content-Type': 'application/json'},
        body: searchTerm
    });

    return await response.json();
  };

  render() {
    const noMoviesFoundMessage = 'This movie was not found. Please search popular movies again with a different term, or search for a new movie.';
    return (
        <div className="App">
            <header>
                <span>Search Popular Movies: <input type="text" onChange={this.searchPopularMovies}/></span>
                <span>Search New Movie: <input type="text" onChange={this.getNewMovie}/></span>
                <button onClick={this.sortByRating}>Sort By Rating</button>
                <button onClick={this.sortByTitle}>Sort By Movie Title</button>
                <button onClick={this.resetFiltering}>Reset</button>
            </header>
            <Table striped bordered hover>
                <thead>
                <tr>
                    <th>id</th>
                    <th>Movie Title</th>
                    <th>Release Data</th>
                    <th>Popularity</th>
                    <th>Average Rating</th>
                    <th>Overview</th>
                </tr>
                </thead>
                <tbody>
                {this.state.movies.map(({id, title, release_date, popularity, vote_average, overview}) => {
                    return <Movie key={id}
                                  title={title}
                                  id={id}
                                  release_date={release_date}
                                  popularity={popularity}
                                  average_rating={vote_average}
                                  overview={overview}/>
                })}
                {this.state.movies.length ? null : <tr><td>{noMoviesFoundMessage}</td></tr>}
                </tbody>
            </Table>
        </div>
    );
  }
}

export default App;
