import React, { Component } from 'react';
import './App.css';
import Movie from "./components/MovieComponent";
import Table from 'react-bootstrap/Table'

class App extends Component {
    state = {
        movies: [],
    };

  componentDidMount() {
      this.getData().then((response) => {
          this.setState({movies: response.results});
          console.log(this.state.movies);
      });
  }

  getData = async () => {
      let response = await fetch('/getPopular');
      return await response.json();
  };

  sortByRating = () => {
      const copyState = {...this.state};
      const sortedByRating = copyState.movies.sort((a, b) => b.vote_average - a.vote_average);
      this.setState({movies: sortedByRating});
  };

  render() {
    return (
        <div className="App">
            <button onClick={this.sortByRating}>Sort By Rating</button>
            <Table size="sm">
                <thead>
                <tr>
                    <th>id</th>
                    <th>Movie Title</th>
                    <th>Release Data</th>
                    <th>Popularity</th>
                    <th>Average Rating</th>
                    <th>Overview</th>
                </tr>
                </thead>
                <tbody>
                {this.state.movies.map(({id, title, release_date, popularity, vote_average, overview}) => {
                    return <Movie key={id}
                                  title={title}
                                  id={id}
                                  release_date={release_date}
                                  popularity={popularity}
                                  average_rating={vote_average}
                                  overview={overview}/>
                })}
                </tbody>
            </Table>
        </div>
    );
  }
}

export default App;
